// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Authorization {
    // Mapping to store user authorizations for admins
    // userAddress => adminAddress => isAuthorized
    mapping(address => mapping(address => bool)) public authorizations;
    
    // Mapping to store admin addresses
    mapping(address => bool) public isAdmin;
    
    // Mapping to store all admins a user has authorized
    // userAddress => adminAddresses[]
    mapping(address => address[]) public userAdmins;
    
    // Mapping to store all users who have authorized an admin
    // adminAddress => userAddresses[]
    mapping(address => address[]) public adminUsers;
    
    // Mapping to track admin index in userAdmins array for efficient removal
    // userAddress => adminAddress => index
    mapping(address => mapping(address => uint256)) public adminIndex;
    
    // Mapping to track user index in adminUsers array for efficient removal
    // adminAddress => userAddress => index
    mapping(address => mapping(address => uint256)) public userIndex;
    
    // Events
    event AdminAuthorized(address indexed user, address indexed admin);
    event AdminRevoked(address indexed user, address indexed admin);
    event AdminRegistered(address indexed admin);
    
    // Constructor to set the initial admin
    constructor(address initialAdmin) {
        isAdmin[initialAdmin] = true;
        emit AdminRegistered(initialAdmin);
    }
    
    // Register a new admin (only callable by existing admins)
    function registerAdmin(address newAdmin) public {
        require(isAdmin[msg.sender], "Only admins can register new admins");
        isAdmin[newAdmin] = true;
        emit AdminRegistered(newAdmin);
    }
    
    // Authorize an admin to access user's funds
    function authorizeAdmin(address admin) public {
        require(isAdmin[admin], "Address is not registered as admin");
        require(!authorizations[msg.sender][admin], "Admin already authorized");
        
        authorizations[msg.sender][admin] = true;
        userAdmins[msg.sender].push(admin);
        adminIndex[msg.sender][admin] = userAdmins[msg.sender].length - 1;
        
        // Add user to admin's user list
        adminUsers[admin].push(msg.sender);
        userIndex[admin][msg.sender] = adminUsers[admin].length - 1;
        
        emit AdminAuthorized(msg.sender, admin);
    }
    
    // Revoke admin access
    function revokeAdmin(address admin) public {
        require(authorizations[msg.sender][admin], "Admin not authorized");
        
        authorizations[msg.sender][admin] = false;
        
        // Remove admin from userAdmins array
        uint256 adminIdx = adminIndex[msg.sender][admin];
        uint256 lastAdminIdx = userAdmins[msg.sender].length - 1;
        
        if (adminIdx != lastAdminIdx) {
            address lastAdmin = userAdmins[msg.sender][lastAdminIdx];
            userAdmins[msg.sender][adminIdx] = lastAdmin;
            adminIndex[msg.sender][lastAdmin] = adminIdx;
        }
        
        userAdmins[msg.sender].pop();
        delete adminIndex[msg.sender][admin];
        
        // Remove user from adminUsers array
        uint256 userIdx = userIndex[admin][msg.sender];
        uint256 lastUserIdx = adminUsers[admin].length - 1;
        
        if (userIdx != lastUserIdx) {
            address lastUser = adminUsers[admin][lastUserIdx];
            adminUsers[admin][userIdx] = lastUser;
            userIndex[admin][lastUser] = userIdx;
        }
        
        adminUsers[admin].pop();
        delete userIndex[admin][msg.sender];
        
        emit AdminRevoked(msg.sender, admin);
    }
    
    // Check if an admin is authorized for a user
    function isAuthorized(address user, address admin) public view returns (bool) {
        return authorizations[user][admin];
    }
    
    // Get all authorized admins for a user
    function getAuthorizedAdmins(address user) public view returns (address[] memory) {
        return userAdmins[user];
    }
    
    // Get all users who have authorized an admin
    function getAuthorizedUsers(address admin) public view returns (address[] memory) {
        return adminUsers[admin];
    }
    
    // Transfer funds from user to admin (admin executes this)
    function transferFromUser(address user, uint256 amount) public {
        require(authorizations[user][msg.sender], "Not authorized to transfer from this user");
        
        // Transfer funds (this is a simplified version)
        // In practice, you would interact with a token contract or use native currency
        payable(msg.sender).transfer(amount);
    }
    
    // Fallback function to receive Ether
    receive() external payable {}
    
    // Function to get contract balance
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }
}

